import React from 'react';
import { connect } from 'react-redux';
import {withRouter} from 'react-router-dom'

import CollectionItem from '../../components/collection-item/collection-item.component';

import { selectCollection } from '../../redux/shop/shop.selectors';


import { CollectionPageContainer, CollectionTitle, CollectionItemsContainer } from './collection.styles';

const CollectionPage = ({ collection, linkUrl, match, history }) => {
  const { title, items } = collection;
  return (
<CollectionPageContainer>
   <CollectionTitle onClick={() => history.push(`${match.url}${linkUrl}`)}>{title}</CollectionTitle>
      <CollectionItemsContainer>
          {items.map(item => (
          <CollectionItem key={item.id} item={item} />
          ))}
     </CollectionItemsContainer>
</CollectionPageContainer>
  );
};

const mapStateToProps = (state, ownProps) => ({
  collection: selectCollection(ownProps.match.params.collectionId)(state)
});

export default withRouter(connect(mapStateToProps)(CollectionPage));
