import styled from 'styled-components';

export const CollectionsOverviewContainer = styled.div`
  display: flex;
  flex-direction: column;
  
  
  @media screen and (max-width: 800px;
    min-width:320px) {
    font-size:12px;
    height: 60px;
    width:100%;
    padding: 10px;
    margin-bottom: 30px;
    top:10px;
`;
